import { NavLink } from "react-router-dom";
import axios from "axios";
import Button from "../components/Button";
import add from "../assets/images/icons/add.png";
import { useState, useEffect } from "react";

export default function Publier() {
  const [successMessage, setSuccessMessage] = useState("");
  const [showErrorMessage, setShowErrorMessage] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [formData, setFormData] = useState({/* Votre objet formData initial */});

  function handleChange(e) {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  }

  const [blood, setBlood] = useState([]);
  const [races, setRaces] = useState([]);
  const [sex, setSex] = useState([]);
  const [locations, setLocations] = useState([]);
  const [eyeColor, setEyeColor] = useState([]);
  const [showPrice, setShowPrice] = useState(true);

  useEffect(() => {
    fetchSelectData();
  }, []);

  const fetchSelectData = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/offers/offers/get_form_data/");
      setRaces(response.data.races);
      setSex(response.data.sexe);
      setLocations(response.data.locations);
      setBlood(response.data.bloodtype);
      setEyeColor(response.data.eye_color);
    } catch (e) {
      console.error("Erreur lors de la rÃ©cupÃ©ration des donnÃ©es");
    }
  };

  function handleFileChange(e) {
    const file = e.target.files[0];
    const name = e.target.name;
    if (file) {
      setFormData({ ...formData, [name]: file });
    }
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    const loofValue = document.querySelector('input[name="loof"]:checked')?.value;
    const vaccinsValue = document.querySelector('input[name="vaccins"]:checked')?.value;

    if (loofValue === "Oui" && vaccinsValue === "Oui") {
      setShowErrorMessage(false);
      axios.post("http://127.0.0.1:8000/offers/offers/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      })
      .then((response) => {
        setFormData(initialFormData);
        setSuccessMessage("Votre annonce a bien Ã©tÃ© postÃ©e!");
      })
      .catch((err) => {
        console.error(err);
      });
    } else {
      setShowErrorMessage(true);
      setErrorMessage("Ce champ est obligatoire. Nous n'acceptons que les chats LOOF et vaccinÃ©s.");
    }
  };

  useEffect(() => {
    setShowPrice(formData.sex !== "Femelle");
  }, [formData.sex]);

  return (
    <>
      <NavLink to="/">
        <div className="ml-5 md:ml-20 mt-16 md:mt-28 flex flex-row text-verydarkgray hover:opacity-80">
          &larr; Retour Ã  l'accueil
        </div>
      </NavLink>
      <div className="container flex flex-col mx-auto animate-fade">
        <div className="container flex flex-row mx-auto mb-6">
          <img
            src={add}
            className="mt-4 md:mt-0.5 z-2 w-6 h-6 ml-auto"
            alt="ajouter une annonce"
          />
          <h2 className="pl-3 mt-4 md:mt-0 mr-auto text-purple text-xl md:text-2xl">
            Ajoutez une annonce:
          </h2>
        </div>
        {successMessage && (
          <div className="border border-green bg-lightgreen mx-96 rounded-lg mt-0 animate-bounce">
            <p className="text-green-600 text-center text-green mt-3 mb-4">
              {successMessage}
            </p>
          </div>
        )}
        <div className="flex flex-col md:flex-row items-center place-items-center my-auto mx-auto">
          <form onSubmit={handleSubmit} className="mx-4 pb-4 md:-ml-20">
            <fieldset className="flex flew-row justify-around md:w-[1000px]">
              <div className="flex flex-col md:w-[500px] md:h-[600px] px-10 bg-white rounded-3xl shadow-2xl">
                <h3 className="text-xl md:text-2xl text-center mt-3 mb-6">
                  Description ðŸ˜½:
                </h3>
                <div className="flex flex-row items-center justify-between mb-3 text-sm md:text-lg">
                  <span>LOOF*:</span>
                  <div className="ml-20">
                    <input type="radio" id="LOOF_yes" name="loof" value="Oui" required />
                    <label htmlFor="LOOF_yes" className="pl-2">Oui</label>
                  </div>
                  <div>
                    <input type="radio" id="LOOF_no" name="loof" value="Non" />
                    <label htmlFor="LOOF_no" className="pl-2 pr-9">Non</label>
                  </div>
                </div>
                {showErrorMessage && (
                  <div className="text-darkyellow border border-darkyellow bg-lightyellow rounded-lg p-2 text-center absolute mt-36 w-96 ml-4 shadow-xl">{errorMessage}</div>
                )}
                <div className="flex flex-row items-center justify-between mb-3 text-sm md:text-lg">
                  <span>Vaccins Ã  jour*:</span>
                  <div className="ml-3">
                    <input type="radio" id="vaccins_yes" name="vaccins" value="Oui" required />
                    <label htmlFor="vaccins_yes" className="pl-2">Oui</label>
                  </div>
                  <div>
                    <input type="radio" id="vaccins_no" name="vaccins" value="Non" />
                    <label htmlFor="vaccins_no" className="pl-2 pr-9">Non</label>
                  </div>
                </div>
                {/* Autres champs de saisie */}
              </div>
            </fieldset>
            <div className="ml-28 mb-16">
              <Button text="Publier" className="w-auto" />
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
