import { NavLink, useLocation } from "react-router-dom";
import users from "../components/Users";
import cats from "../components/Cats";
import change from "../assets/images/icons/change.png";

export default function Messages() {
  const suzanne = users.find((user) => user.name === "Suzanne");
  const popeye = cats.find((cat) => cat.name === "Popeye");
  return (
    <>
      <NavLink to="/">
        <div className="ml-20 mt-28 flex flex-row text-verydarkgray hover:opacity-80">
          &larr; Retour Ã  l'accueil
        </div>
      </NavLink>
      <div className="flex flex-row mt-4 mx-auto">
        <div>
          {/* Owner */}
          <div className="container flex flex-col w-[250px] h-[263px] mr-6 bg-white rounded-3xl shadow-lg pb-5">
            <p className="mx-auto mt-3 mb-2 font-semibold text-lg">
              {suzanne.name}
            </p>
            <div className="w-44 h-44 mx-auto">
              <img
                src={suzanne.image}
                className="w-full h-full object-cover rounded-full"
                alt={suzanne.name}
              />
            </div>
            <div className="mb-4 relative">
              <img
                src={change}
                className=" w-8 ml-48 -mt-3"
                alt="modifier le profil"
              />
            </div>
          </div>
		  {/* title offer */}
          <div className="container flex flex-col w-[250px] h-[50px] mt-6 mr-6 bg-white rounded-3xl shadow-lg pb-5">
			<h3 className="mx-auto pt-3">L'annonce:</h3>
		  </div>
		  {/* The offer vignette */}
          <div className="">
		  <div
          className="vignette flex flex-col justify-center items-center text-center bg-white z-0 md:h-96 w-72 mx-auto mr-3 mt-3 pb-2 rounded-3xl shadow-xl hover:opacity-85"
        >
          <p className="text-center text-lg font-semibold mt-3">{popeye.name}</p>
          <img
            src={popeye.image}
            className="z-20 h-40 md:h-3/4 w-56 my-3 object-cover mx-auto shadow-sm rounded-3xl"
            alt={popeye.name}
          />

            <p className=" flex flex-row text-center text-lg font-semibold mb-1">
              {popeye.race}
              {popeye.sexe}
            </p>
        </div>
		  </div>
        </div>
        {/* Chat */}
        <div className="w-[750px] h-auto mr-6 bg-white rounded-3xl shadow-lg"></div>
        {/* own profile */}
        <div>
          <div className="container flex flex-col w-[250px] h-[263px] bg-white rounded-3xl shadow-lg pb-5">
            <p className="mx-auto mt-3 mb-2 font-semibold text-lg">
              {suzanne.name}
            </p>
            <div className="w-44 h-44 mx-auto">
              <img
                src={suzanne.image}
                className="w-full h-full object-cover rounded-full"
                alt={suzanne.name}
              />
            </div>
            <div className="mb-4 relative">
              <img
                src={change}
                className=" w-8 ml-48 -mt-3"
                alt="modifier le profil"
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
