import React, { useState } from "react";

export default function Carousel() {
  const [startIndex, setStartIndex] = useState(0);
  const images = [
    require('../assets/images/grid-carousel/abyssin.png'),
    require('../assets/images/grid-carousel/bengal.png'),
    require('../assets/images/grid-carousel/britishlonghair.png'),
    require('../assets/images/grid-carousel/ragdoll.png'),
    require('../assets/images/grid-carousel/siamois.png'),
    require('../assets/images/grid-carousel/scottishfold.png'),
    require('../assets/images/grid-carousel/britishshorthair.png'),
  ];

  const nextImages = () => {
    setStartIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImages = () => {
    setStartIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="">
      <div className="bg-white w-80 md:w-[38rem] lg:w-[45rem] pt-2 pb-1.5 border-t-2 border-purple rounded-e-3xl shadow-lg">
        <h2 className="text-lg md:text-2xl text-purple text-center uppercase">Annonces Ã  la Une</h2>
      </div>

      {/* Carousel */}
      <div className="relative mt-4 md:p-4 shadow-xl mb-6 flex bg-white rounded-3xl">
        {images.map((image, index) => (
          <div key={index} className="container flex flex-col bg-white z-0 h-96 w-96 mx-auto mr-3 rounded-3xl shadow-xl">
            <img src={image} className="z-20 h-64 md:h-72 mt-12 object-cover mx-auto" alt={`${index + 1}`} />
          </div>
        ))}
        <button className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-purple py-3 px-5 rounded-full text-white shadow-xl hover:opacity-60 opacity-90" onClick={prevImages}>
          &lt;
        </button>
        <button className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-purple py-3 px-5 rounded-full text-white shadow-xl hover:opacity-60 opacity-90" onClick={nextImages}>
          &gt;
        </button>
      </div>
    </div>
  );
}
