import React, { useState, useEffect } from "react";
import Abyssin from '../assets/images/grid-carousel/abyssin.png';
import Bengal from '../assets/images/grid-carousel/bengal.png';
import BritishLonghair from '../assets/images/grid-carousel/britishlonghair.png';
import BritishShorthair from '../assets/images/grid-carousel/britishshorthair.png';
import Ragdoll from '../assets/images/grid-carousel/ragdoll.png';
import Siamois from '../assets/images/grid-carousel/siamois.png';
import Scottish from '../assets/images/grid-carousel/scottishfold.png';

export default function Carousel() {
    const images = [
        {
            name: "Popeye",
            image: Abyssin,
            race: "Abyssin",
            sexe: "mâle",
        },
        {
            name: "Sahel",
            image: Bengal,
            race: "Bengal",
            sexe: "Mâle",
        },
        {
            name: "Sally",
            image: BritishLonghair,
            race: "British Longhair",
            sexe: "Femelle",
        },
        {
            name: "Christmas",
            image: Ragdoll,
            race: "Ragdoll",
            sexe: "mâle",
        },
        {
            name: "Shtroumpf",
            image: BritishShorthair,
            race: "British short hair",
            sexe: "mâle",
        },
        {
            name: "Apollo",
            image: Siamois,
            race: "Siamois",
            sexe: "mâle",
        },
        {
            name: "Maestro",
            image: Scottish,
            race: "Scottish Fold",
            sexe: "mâle",
        },
    ];

    const [startIndex, setStartIndex] = useState(0);

    useEffect(() => {
        const handleResize = () => {
            const itemsPerRow = calculateItemsPerRow();
            setStartIndex(prevIndex => Math.min(prevIndex, images.length - itemsPerRow));
        };
        window.addEventListener("resize", handleResize);
        handleResize();
        return () => window.removeEventListener("resize", handleResize);
    }, []);

    const calculateItemsPerRow = () => {
        if (window.innerWidth >= 768) {
            return 3; // Par exemple, 3 éléments par rangée pour les écrans larges
        } else {
            return 1; // Pour les écrans plus petits, affichez un élément par rangée
        }
    };

    const nextImages = () => {
        const itemsPerRow = calculateItemsPerRow();
        setStartIndex(prevIndex => (prevIndex + itemsPerRow) % images.length);
    };

    const prevImages = () => {
        const itemsPerRow = calculateItemsPerRow();
        setStartIndex(prevIndex => (prevIndex - itemsPerRow + images.length) % images.length);
    };

    return (
        <div className="max-w-screen-xl mx-auto">
            <div className="text-center mt-8">
                <h2 className="text-lg md:text-xl text-purple uppercase">Annonces à la Une</h2>
            </div>

            <div className="relative mt-4 md:mt-8 md:p-5 shadow-xl mb-5 bg-white rounded-3xl font-semibold overflow-hidden">
                {images.map((image, index) => (
                    <div key={index} className={`inline-block w-full md:w-1/${calculateItemsPerRow()} p-4 md:p-2 md:w-full md:mb-0 transform md:translate-x-${startIndex * 100 / calculateItemsPerRow()} transition-transform`}>
                        <div className="text-center">{image.name}</div>
                        <img src={image.image} className="mx-auto h-48 md:h-64 object-cover" alt={image.name} />
                        <div className="text-center">{image.race}</div>
                    </div>
                ))}
                <button className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-purple py-3 px-5 rounded-full text-white shadow-xl hover:opacity-60 opacity-90" onClick={prevImages}>&lt;</button>
                <button className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-purple py-3 px-5 rounded-full text-white shadow-xl hover:opacity-60 opacity-90" onClick={nextImages}>&gt;</button>
            </div>
        </div>
    );
}
