import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import paw from "../assets/images/icons/paw.png";

export default function Navbar() {
  const [activeNavItem, setActiveNavItem] = useState(null);

  // Fonction pour mettre à jour l'élément actif
  const handleNavItemClick = (itemName) => {
    setActiveNavItem(itemName);
  };

  return (
    <header className="bg-purple z-50 fixed top-0 w-full shadow-xl">
      <nav
        id="header-nav"
        className="bg-purple mx-auto p-0 flex items-center justify-between">
        {/* Logo */}
        <Link
          to="/"
          className="flex "
          aria-label="Page d'accueil de PetMeeting"
        >
          <img
            src={require("../assets/images/logo.png")}
            className="w-52 md:w-60 lg:w-80 md:mr-4"
            alt="Logo de PetMeeting"
          />
        </Link>
        {/* Menu */}
        <ul
          id="toggled-menu"
          className={`w-full absolute flex top-full text-white bg-purple left-0 -z-10 border-b border-gray-200 md:flex flex-col items-center md:static md:z-10 md:w-min md:transform-none md:border-none md:flex-row md:mr-6 ${isMenuOpen ? 'block' : 'hidden'}`}
        >
          <li className="py-4 md:py-0 md:mr-6 hover:animate-wiggle">
            <span className={activeNavItem === "annonces" ? 'z-10 relative' : 'hidden'}>
              <img src={paw} alt="paw" className="absolute -top-2 left-1" />
            </span>
            <Link
              to="/Annonces"
              className="z-10 relative text-sm lg:text-xl uppercase font-semibold rounded-lg px-2 ml-1 w-full"
              onClick={() => handleNavItemClick("annonces")}
            >
              annonces
            </Link>
          </li>
          {/* Autres éléments du menu */}
        </ul>
        {/* Reste de la barre de navigation */}
      </nav>
    </header>
  );
}
