import React, { useState } from 'react';

export default function Carousel() {
  const [startIndex, setStartIndex] = useState(0);
  const images = [
    'https://via.placeholder.com/400x200?text=Image+1',
    'https://via.placeholder.com/400x200?text=Image+2',
    'https://via.placeholder.com/400x200?text=Image+3',
    'https://via.placeholder.com/400x200?text=Image+4',
    'https://via.placeholder.com/400x200?text=Image+5',
  ];

  function nextImages () {
    setStartIndex((prevIndex) => (prevIndex + 1 >= images.length ? 0 : prevIndex + 1));
  };

  const prevImages = () => {
    setStartIndex((prevIndex) => (prevIndex - 1 < 0 ? images.length - 1 : prevIndex - 1));
  };

  return (
    <div className="my-8">
      <div className="bg-white w-80 md:w-[38rem] lg:w-[45rem] pt-2 pb-1.5 border-t-2 border-purple rounded-e-3xl shadow-lg">
        <h2 className="text-lg md:text-2xl text-purple text-center uppercase">Annonces Ã  la Une</h2>
      </div>

      {/* Carousel */}
      <div className="relative mt-4 flex">
        {images.slice(startIndex, startIndex + 5).map((image, index) => (
          <img key={index} src={image} className="w-1/5 h-64 md:h-80 object-cover" alt={`Image ${index + 1}`} />
        ))}
        <button className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-purple p-2 rounded-full text-white" onClick={prevImages}>
          &lt;
        </button>
        <button className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-purple p-2 rounded-full text-white" onClick={nextImages}>
          &gt;
        </button>
      </div>
    </div>
  );
}
