        <img src={lock} className="absolute -ml-72 mb-4" alt="lock"></img>
        <input
          type="text"
          placeholder="Mot de passe*"
          className="h-12 w-[90%] ml-4 mb-4 pt-1 pl-8 border-b border-darkgray focus:outline-none"
        />
      </div>
